import React from 'react';
import styled from 'styled-components';
import AlarmItem from "./AlarmItem";
import { useAlarmState } from '../AlarmContext';

const AlarmListBlock = styled.div`
  flex: 1;
  padding: 20px 32px;
  padding-bottom: 48px;
  overflow-y: auto;
`;

function AlarmList() {
  const alarms = useAlarmState();
  return (

  <AlarmListBlock> <h2>발매 알림 리스트</h2>
      {/* <AlarmItem date="4월 30일 발매" done={true} /> */}
      {/* <AlarmItem product="4월 30일 발매" done={true} /> */}
      {/* <AlarmItem date="5월 30일 발매" done={false} />
      <AlarmItem date="7월 30일 발매" done={true} />
      <AlarmItem date="8월 30일 발매" done={true} />
      <AlarmItem date="9월 30일 발매" done={true} />
      <AlarmItem date="10월 30일 발매" done={true} /> */}
      {alarms.map(alarm => (
        <AlarmItem
          key={alarm.id}
          id={alarm.id}
          date={alarm.date}
          done={alarm.done}
        />
      ))}
  </AlarmListBlock>
  );
}

export default AlarmList;
