import React from 'react';
import styled, { css } from 'styled-components';
import { ImImage, ImBin2 } from 'react-icons/im';
import { products } from "../Dummydata/Dummy.js"; // APT로 불러 올 부분

const Remove = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  color: #dee2e6;
  font-size: 24px;
  cursor: pointer;
  &:hover {
    color: #ff6b6b;
  }
  display: none;
`;

const AlarmItemBlock = styled.div`
  display: flex;
  align-items: center;
  padding-top: 12px;
  padding-bottom: 12px;
    ${Remove} {
      display: initial;
    }
  }
`;

const ItemImg = styled.div`
  width: 96px;
  height: 96px;
  border: 1px solid #ced4da;
  font-size: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 20px;
  cursor: pointer;
  ${props =>
    props.done &&
    css`
      border: 1px solid #000000;
      color: #000000;
    `}
`;

const Text = styled.div`
  flex: 1;
  font-size: 24px;
  font-weight: 500;
  color: #000000;
  ${props =>
    props.done &&
    css`
      color: #000000;
    `}
`;

const Text2 = styled.div`
  flex: 1;
  font-size: 24px;
  font-weight: 500;
  color: #000000;
  ${props =>
    props.done &&
    css`
      color: #000000;
    `}
`;

function AlarmItem({ id, done, text }) {
  return (
    <AlarmItemBlock>
      <ItemImg done={done}>{done && <ImImage />}</ItemImg>
      <Text done={done}>{text}</Text>
      <Remove>
        <ImBin2 />
      </Remove>
    </AlarmItemBlock>
  );
}

export default AlarmItem;
